import React, { useState } from 'react'

function Form() {
    const [form, setForm] = useState({ name: '', cidade: '', age: 0 });

    const handleClick = (event) => {
        event.preventDefault()
    };

    const handleChange = ({ target: { name, value } }) => {
        setForm({
            ...form,
            [name]: value,
        })
    };

  return (
    <form>
        <fieldset>
        <label htmlFor="input-name">
            Nome completo:
        <input
          type="text"
          name="name"
          id="input-name"
          value={ form.name }
          onChange={ handleChange }
          />
        </label>
        <br />

        <label htmlFor="input-name">
            Cidade:
        <input
          type="text"
          name="cidade"
          id="input-city"
          value={ form.city }
          onChange={ handleChange }
          />
        </label>
        <br />

        <label htmlFor="input-age">
            Idade:
        <input
          type="number"
          name="age"
          id="input-age"
          value={ form.age }
          onChange={ handleChange }
          />
        </label>
        <br />


        <h2>Módulos já estudados</h2>

        <input type="radio" />Fundamentos
        <br />
        <input type="radio" />Front-End
        <br />
        <input type="radio" />Back-End
        <br />
        <input type="radio" />Ciência da Computação
        <br />

        <button
          type="submit"
          onClick={ handleClick }
        >
            Enviar
        </button>
        </fieldset>
    </form>
  )
}
export default Form;
