import React, { useState } from 'react'

function Form() {
    const [form, setForm] = useState({ name: '', cidade: '', age: 0 });
    const [module, setModule] = useState();

    const handleClick = (event) => {
        event.preventDefault()
    };

    const handleChange = ({ target: { name, value } }) => {
        setForm({
            ...form,
            [name]: value,
        })
    };

  return (
    <form>
        <fieldset>

            <legend>Dados Pessoais</legend>
        <label htmlFor="name">
            Nome completo:
        <input
          type="text"
          name="name"
          id="name"
          value={ form.name }
          onChange={ handleChange }
          />
        </label>
        <br />

        <label htmlFor="cidade">
            Cidade:
        <input
          type="text"
          name="cidade"
          id="cidade"
          value={ form.city }
          onChange={ handleChange }
          />
        </label>
        <br />

        <label htmlFor="age">
            Idade:
        <input
          type="number"
          name="age"
          id="age"
          value={ form.age }
          onChange={ handleChange }
          />
        </label>
        <br />


        <h2>Módulos já estudados</h2>

        <label htmlFor="fundamentos">
            Fundamentos
        <input
          type="radio"
          id="fundamentos"
          value="Fundamentos"
          checked={ module === "Fundamentos" }
          onChange={(target) => setModule(target.module)}
          />
        </label>

        <br />
        <input type="radio" />Front-End
        <br />
        <input type="radio" />Back-End
        <br />
        <input type="radio" />Ciência da Computação
        <br />

        <button
          type="submit"
          onClick={ handleClick }
        >
            Enviar
        </button>
        </fieldset>
    </form>
  )
}
export default Form;
