import './App.css';
import { useState } from 'react';
import messagesList from './data/messagesList';
import List from './components/List';

function App() {
  const [messages, setMessages] = useState(messagesList);

  const setMessageStatus = (messageId, newStatus) => {
    const upDatedMessages = messages.map((currentMessage) => (
      currentMessage.id === messageId ? { ...currentMessage, status: newStatus } : currentMessage
    ));

    setMessages(upDatedMessages);
  }

  return (
    <div className="App">
      <header>
        <h1>TrybeMail</h1>
      </header>

      <List
        messages={ messages }
        setMessageStatus={ setMessageStatus }
        />
    </div>
  );
}

export default App;
